        int_id_ticket_canal,    /*id_cat_ticket_canal*/
        int_id_ticket_prioridad,/*id_cat_ticket_prioridad*/
        int_estado,
        int_creado_por,
        int_actualizado_por,
        now(),
        now()
    )
RETURNING nova_ticket.tbl_bit_ticket.referencia;
END
$$;


