        tctt.id_cat_ticket_tipo,
        tbt.referencia,
        tbt.referencia_ticket_padre,
        tctc.nombre AS canal,
        tctc.id_cat_ticket_canal,
        tbt.resumen,
        tbt.descripcion,
        tctp.nombre AS prioridad,
